import React from "react";

const Register = ({ isJoining, userData, handleValue, registerUser }) => {
  const handleRegKeyPress = (event) => {
    if (event.keyCode === 13 || event.which === 13) {
      registerUser();
    }
  };

  return (
    <div className=" flex flex-col items-center h-screen justify-center">
      <h1 className="mb-24 text-6xl text-[#FFFFFF] font-extrabold">livegab</h1>
      <input
        name="username"
        type="text"
        maxLength={50}
        onKeyPress={handleRegKeyPress}
        className="sm:w-[30%] w-[60%] h-[5%] border drop-shadow-lg text-slate-400 rounded bg-[#282A3A] border-[#7d94b5] text-center mb-4"
        placeholder="Enter your username"
        value={userData.username}
        onChange={handleValue}
      />
      {isJoining ? (
        <button
          id="reg-btn"
          onClick={registerUser}
          className="glass drop-shadow-lg sm:w-[30%] w-[60%] h-[5%] cursor:pointer bg-[#007acc] text-white rounded hover:bg-[#2790d5]"
        >
          Join
        </button>
      ) : (
        <button
            id="reg-btn"
            type="button"
          className="glass disabled drop-shadow-lg sm:w-[30%] w-[60%] h-[5%] cursor:pointer bg-[#007acc] text-white rounded hover:bg-[#2790d5]"
        >
          <svg class="animate-spin h-5 w-5 mr-3 ..." viewBox="0 0 24 24"></svg>...
        </button>
      )}
    </div>
  );
};

export default Register;
