import React, { useEffect, useRef, useState } from 'react';
import MemberList from './MemberList';
import ChatContent from './ChatContent';
import MessageInput from './MessageInput';

const ChatBox = ({
  tab,
  userData,
  publicChats,
  privateChats,
  handleValue,
  sendPublicMessage,
  sendPrivateMessage,
  setTab,
  onConnected
}) => {
  const chatContentRef = useRef(null);
  const [sidebarOpen, setSidebarOpen] = useState(false);

  const autoScroll = () => {
    if (
      chatContentRef.current &&
      chatContentRef.current.scrollTop >=
        chatContentRef.current.scrollHeight - window.screen.height * 0.92
    ) {
      chatContentRef.current.scrollTop = chatContentRef.current.scrollHeight;
    }
  };

  const initialScroll = () => {
    if (document.documentElement.scrollTop = 0) {
      chatContentRef.current.scrollTop = chatContentRef.current.scrollHeight;
    }
  };

  useEffect(() => {
    initialScroll();
  }, []);

  useEffect(() => {
    autoScroll();
  }, [publicChats, privateChats]);

  const handleSidebarToggle = () => {
    setSidebarOpen(!sidebarOpen);
  };

  return (

    <div className=''>
      <div className=''>
        <button onClick={handleSidebarToggle} className='fixed top-0 z-20 sm:hidden block'>
          Bbbb
        </button>
      </div>
      {!sidebarOpen ? null : (
        <div className='fixed left-0 z-10 h-screen bg-primary w-[60%]'>
          <MemberList tab={tab} privateChats={privateChats} setTab={setTab} userData={userData} />
        </div>
      )}

      <div className='Chatbox h-screen flex flex-col-1'>
        <div className='SideBar w-full max-w-[20%] bg-[#282b30] hidden sm:block border-[#ffffff29] text-white shadow-lg'>
          <MemberList tab={tab} privateChats={privateChats} setTab={setTab} userData={userData} />
        </div>
        <div className='h-[90%] sm:ml-0 ml-8 w-screen overflow-y-auto' ref={chatContentRef}>
          <ChatContent
            tab={tab}
            userData={userData}
            publicChats={publicChats}
            privateChats={privateChats}
            handleValue={handleValue}
            sendPublicMessage={sendPublicMessage}
            sendPrivateMessage={sendPrivateMessage}
          />
          <div className='messageSender mb-4 pr-4 h-[8%] sm:w-[79%] w-[98%] fixed bottom-0 right-0'>
            <MessageInput
              tab={tab}
              userData={userData}
              publicChats={publicChats}
              privateChats={privateChats}
              handleValue={handleValue}
              sendPublicMessage={sendPublicMessage}
              sendPrivateMessage={sendPrivateMessage}
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default ChatBox;
