import React, { useEffect } from 'react';

const Register = ({ userData, handleValue, registerUser }) => {
  useEffect(() => {
    // Get the button element
    const button = document.querySelector('.register-button');

    // Add event listener to the button element
    const handleClick = () => {
      registerUser();
    };

    button.addEventListener('click', handleClick);

    // Clean up the event listener when the component unmounts
    return () => {
      button.removeEventListener('click', handleClick);
    };
  }, [registerUser]);

  return (
    <div className="flex flex-col items-center h-screen justify-center">
      <h1 className='mb-24 text-6xl text-[#FFFFFF] font-extrabold'>livegab</h1>
      <input
        name="username"
        type='text'
        className="input-accent w-[30%] h-[5%] border rounded bg-[#282A3A] border-[#7d94b5] text-center mb-4"
        placeholder="Enter your user name"
        value={userData.username}
        onChange={handleValue}
      />
      <button className="glass z-50 w-[30%] h-[5%] cursor:pointer bg-[#007acc] text-white rounded hover:bg-[#2790d5] register-button">
        Join
      </button>
    </div>
  );
};

export default Register;
