import React, { useEffect } from "react";

const MemberList = ({ tab, privateChats, setTab, userData }) => {

        useEffect(() => {}, [privateChats]);

  return (
            <div className="member-list text-[#8d8f92] text-center">
                <ul>
                    <li onClick={()=>{setTab("CHATROOM")}} className={`member ${tab==="CHATROOM" && "active"} max-w-[100%] break-words   mb-4`}>Chatroom</li>
                    <li className=' border-[#ffffff29] mb-2'>Signed in as: {userData.username}</li>
                    <li className="max-w-[100%] mt-4 break-words rounded font-thin mb-4">DIRECT MESSAGES</li>
                    {[...privateChats.keys()].map((name,index)=>(
                    <li onClick={()=>{setTab(name)}} className={`member ${tab===name && "active"} mb-1 hover:bg-primary hover:text-white`} key={index}> {name === userData.username ? '' : name} </li>
                    ))}
                </ul>

            </div>
  );


};

export default MemberList;
