import React from 'react';
import MemberList from "../Components/MemberList";
import ChatContent from "../Components/ChatContent";

const ChatBox = ({
    tab,
    userData,
    publicChats,
    privateChats,
    handleValue,
    sendPublicMessage,
    sendPrivateMessage,
    setTab,
}) => {
    return (
        <div className="flex flex-col h-screen">
            {/* Sidebar */}
            <div className="SideBar fixed top-0 left-0 h-screen w-24 m-0 bg-gray-600 text-white shadow-lg text-center">
                <MemberList tab={tab} privateChats={privateChats} setTab={setTab} />
            </div>

            {/* Main Content */}
            <div className="flex-1 overflow-y-auto">
                <ChatContent
                    tab={tab}
                    userData={userData}
                    publicChats={publicChats}
                    privateChats={privateChats}
                    handleValue={handleValue}
                    sendPublicMessage={sendPublicMessage}
                    sendPrivateMessage={sendPrivateMessage}
                />
            </div>

            {/* Send Message Textbox */}
            <div className="fixed bottom-0 left-0 right-0 p-4 bg-gray-200">
                <input
                    className="w-full px-3 py-2 bg-white rounded border focus:outline-none focus:shadow-outline"
                    type="text"
                    placeholder="Type your message..."
                />
            </div>
        </div>
    );
}

export default ChatBox;
