import React, { useEffect, useRef, useState } from "react";

const MemberList = ({ tab, privateChats, setTab, userData }) => {
  useEffect(() => {
    // Your useEffect logic here
  }, [privateChats]);

  const privateChatUsers = [...privateChats.keys()].filter(
    (name) => name !== userData.username
  );

  const [containerHeight, setContainerHeight] = useState("80vh");
  const containerRef = useRef(null);

  // Calculate and set the maximum height dynamically
  useEffect(() => {
    if (containerRef.current) {
      const divHeight = containerRef.current.offsetHeight;
      // You can adjust the calculation as needed, for example, subtracting padding/margin
      const maxHeight = divHeight * 0.8 + "px"; // Set the maximum height to 80% of the div height
      setContainerHeight(maxHeight);
    }
  }, [privateChats]);

  return (
    <div className="member-list cursor-default text-[#8d8f92] flex justify-center">
      <ul className="overflow-y-auto">
        <li
          onClick={() => {
            setTab("CHATROOM");
          }}
          className="max-w-[100%] break-words cursor-pointer mt-6 hover:bg-primary hover:rounded-md hover:text-[#ffffffb9] font-thin md:text-lg text-xs"
        >
          PUBLIC MESSAGES
        </li>
        <li className="max-w-[100%] mt-6 mb-2 break-words rounded font-thin md:text-lg text-xs">
          DIRECT MESSAGES
        </li>
        {/* Dynamic maximum height based on the height of the div below it */}
        <div
          className="overflow-y-auto"
          style={{ maxHeight: containerHeight }}
          ref={containerRef}
        >
          {privateChatUsers.map((name, index) => (
            <li
              onClick={() => {
                setTab(name);
              }}
              className="py-1 break-all mr-1 ml-1 cursor-pointer hover:bg-primary hover:rounded-md hover:text-[#ffffffb9] md:text-base text-xs"
              key={index}
            >
              {name === userData.username ? "" : name}
            </li>
          ))}
        </div>
      </ul>
      <div className="fixed bottom-0 break-all font-thin mb-2 text-center border-[#ffffff29] mt-4">
        Signed in as: {userData.username}
      </div>
    </div>
  );
};

export default MemberList;
